function code(elem) {
  let newElement = document.createElement("div"),
      parent = elem.parentNode;

  newElement.classList.add("codes");

  newElement.innerHTML =
    "<div className='codes' ><i id='codeCopy' contentEditable='false'class='far fa-clipboard'></i><p contentEditable='true' className='code'>code here</p></div>";

  parent.replaceChild(newElement, elem);

  newElement.focus();
  copyCode();
}

function copyCode() {
  var btn=document.querySelector("#codeCopy");

  btn.addEventListener("click",() =>{
    alert(btn.parentElement.nodeName)
  })



}

export { code };
