import "./control.css"
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

const options = [
    'one', 'two', 'three'
  ];


class Container extends React.Component
{
   constructor(props){
       super(props);
   }


   componentDidMount() {
    this.initialize();
   }

   initialize() {
        alert("script initialised")
   }

   selectFont() {
    alert("font selected")
}





   render() {
       return(
        <div className="panel">




        <Dropdown options={options} onChange={this.selectFont} value={0} placeholder="Select an option" />





        </div>
       );
   }
}


















export default Container
